// Initialize technical details validation
function initializeTechnicalDetailsValidation() {
  const technicalDetailsFields = [
    {
      id: 'activityFocus',
      rules: ['optional'],
      messages: {}
    },
    {
      id: 'businessDescription',
      rules: ['optional', 'minLength:10', 'maxLength:1500'],
      messages: {
        minLength: 'Business description must be at least 10 characters long',
        maxLength: 'Business description cannot exceed 1500 characters'
      }
    },
    {
      id: 'migrationPhase',
      rules: ['optional'],
      messages: {}
    },
    {
      id: 'salesforceLink',
      rules: ['optional', 'validUrl'],
      messages: {
        validUrl: 'Please enter a valid URL (e.g., https://example.salesforce.com/...)'
      }
    },
    {
      id: 'awsCalculatorLink',
      rules: ['optional', 'validUrl'],
      messages: {
        validUrl: 'Please enter a valid URL (e.g., https://calculator.aws/...)'
      }
    }
  ];

  technicalDetailsFields.forEach(field => {
    const element = document.getElementById(field.id);
    const errorElement = document.getElementById(`${field.id}-error`);
    const successElement = document.getElementById(`${field.id}-success`);
    const indicatorElement = document.getElementById(`${field.id}-indicator`);

    if (element) {
      // Real-time validation on change/input
      element.addEventListener('change', () => {
        validateTechnicalDetailsField(field, element, errorElement, successElement, indicatorElement);
      });

      element.addEventListener('input', () => {
        validateTechnicalDetailsField(field, element, errorElement, successElement, indicatorElement);

        // Character count for business description textarea
        if (field.id === 'businessDescription') {
          updateBusinessDescriptionCharacterCount(element);
        }
      });

      // Validation on blur
      element.addEventListener('blur', () => {
        validateTechnicalDetailsField(field, element, errorElement, successElement, indicatorElement);
      });

      // Initial character count setup for business description
      if (field.id === 'businessDescription') {
        updateBusinessDescriptionCharacterCount(element);
      }
    }
  });
}

// Validate technical details fields
function validateTechnicalDetailsField(fieldConfig, element, errorElement, successElement, indicatorElement) {
  const value = element.value.trim();
  let isValid = true;
  let errorMessage = '';

  // Check each validation rule
  for (const rule of fieldConfig.rules) {
    if (rule === 'optional') {
      // Optional fields are always valid, but we still check other rules if value exists
      continue;
    } else if (rule.startsWith('minLength:') && value) {
      const minLength = parseInt(rule.split(':')[1]);
      if (value.length < minLength) {
        isValid = false;
        errorMessage = fieldConfig.messages.minLength;
        break;
      }
    } else if (rule.startsWith('maxLength:') && value) {
      const maxLength = parseInt(rule.split(':')[1]);
      if (value.length > maxLength) {
        isValid = false;
        errorMessage = fieldConfig.messages.maxLength;
        break;
      }
    } else if (rule === 'validUrl' && value) {
      if (!isValidUrl(value)) {
        isValid = false;
        errorMessage = fieldConfig.messages.validUrl;
        break;
      }
    }
  }

  // Update UI based on validation result
  updateTechnicalDetailsFieldUI(element, errorElement, successElement, indicatorElement, isValid, errorMessage, value);

  return isValid;
}

// Update technical details field validation UI
function updateTechnicalDetailsFieldUI(element, errorElement, successElement, indicatorElement, isValid, errorMessage, value) {
  // Reset classes
  element.classList.remove('valid', 'invalid');

  if (value) {
    if (isValid) {
      element.classList.add('valid');
      if (errorElement) {
        errorElement.textContent = '';
        errorElement.style.display = 'none';
      }
      if (successElement) {
        successElement.textContent = 'Valid';
        successElement.style.display = 'block';
      }
      if (indicatorElement) {
        indicatorElement.textContent = '';
        indicatorElement.className = 'validation-indicator valid';
      }
    } else {
      element.classList.add('invalid');
      if (errorElement) {
        errorElement.textContent = errorMessage;
        errorElement.style.display = 'block';
      }
      if (successElement) {
        successElement.style.display = 'none';
      }
      if (indicatorElement) {
        indicatorElement.textContent = '';
        indicatorElement.className = 'validation-indicator invalid';
      }
    }
  } else {
    // Empty field - reset to neutral state (valid for optional fields)
    if (errorElement) {
      errorElement.textContent = '';
      errorElement.style.display = 'none';
    }
    if (successElement) {
      successElement.style.display = 'none';
    }
    if (indicatorElement) {
      indicatorElement.textContent = '';
      indicatorElement.className = 'validation-indicator';
    }
  }
}

// Update character count for business description textarea
function updateBusinessDescriptionCharacterCount(element) {
  const countElement = document.getElementById(`${element.id}-count`);
  const maxLength = 1500;
  const currentLength = element.value.length;

  if (countElement) {
    countElement.textContent = currentLength;

    const countContainer = countElement.parentElement;
    countContainer.classList.remove('warning', 'danger');

    if (currentLength > maxLength * 0.9) {
      countContainer.classList.add('danger');
    } else if (currentLength > maxLength * 0.75) {
      countContainer.classList.add('warning');
    }
  }
}

